import React, { Component } from 'react';
import {Redirect} from 'react-router-dom'
import ResponsiveDrawer from './Layout';

class IndexTutorial extends Component {

  constructor(props){
    super(props)
    this.state={
      tutorialIndex:[],
      tutoName:'',
    }
  }



  componentDidMount(){
    const api='/api';
    let res='';
    switch(this.props.match.path){
      case '/react':
        res = api.concat('/react');
        this.setState({tutoName:'React'})
        break;
      case '/redux':
           res = api.concat('/redux');
           this.setState({tutoName:'Redux'})
          break;
      default:
        return null;
    }
    fetch("https://tutorialweb-backend.herokuapp.com"+res)
      .then(res=>res.json())
      .then(response=>this.setState({tutorialIndex:response}));
  }


  render() {
    console.log('IndexTutorial props',this.props);
    return (
      <div>

        <ResponsiveDrawer
         tutoName={this.state.tutoName}
         tutorialIndex={this.state.tutorialIndex}
         />


      </div>
    );
  }
}

export default IndexTutorial;
