import React, { Component } from 'react';

import ResponsiveDrawer from './Layout';

function getReactIndex(){
  fetch('/api/react')
  .then(res=>res.json())
  .then(response=>this.setState({reactTutorialIndex:response}))
}

class IndexTutorial extends Component {
  constructor(props){
    super(props)
    this.state={
      reactTutorialIndex:[],
      reduxTutorialIndex:[]
    }

  }



  render() {
    switch (this.props.match.url) {
      case '/react':
        getReactIndex()
        return <ResponsiveDrawer tutoName="React" tutorialIndex={this.state.reactTutorialIndex} />
        break
      case '/redux':

        return  <ResponsiveDrawer tutoName="Redux" tutorialIndex={this.state.reduxTutorialIndex} />
        break
      default:
        return null
    }
    return (
      <div>
        {console.log(this.props.match.url)}
      </div>
    );
  }
}

export default IndexTutorial;
