import React from 'react'
import  { useState, useEffect } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';



class ReactHome extends React.Component {

  constructor(props){
    super(props)
    this.state={
      TopicContents:[]
    }
    fetch('/api/react/getLessons')
    .then(res=>res.json())
    .then(response=>this.setState({TopicContents:response}))
  }



render(){
  let componentVal = this;
  const urlParam=this.props.match.params;
  let tutorialName = this.state.TopicContents.filter(function(topic){
    return (topic.url_id === componentVal.props.match.params.topicId)
  })

  {console.log(this.props.match.params.topicId)}



  return (

    <div>

    <br/><br/><br/>
      <Paper >

      {tutorialName.map(lesson=>(
        <div key={lesson._id}>
        {lesson.name}<br/>
        <div dangerouslySetInnerHTML={{ __html: lesson.lesson_description}} />
        
        </div>
      ))}
      </Paper>
    </div>
  )
}
}

export default ReactHome
