import React, { Component } from 'react';
import ResponsiveDrawer from './Layout';

class IndexTutorial extends Component {

  constructor(props){
    super(props)
    this.state={
      tutorialIndex:[],
      tutoName:'',
      imageIcon:''
    }
  }



  componentDidMount(){
    const api='/api';
    let res='';
    switch(this.props.match.path){
      case '/react':
        res = api.concat('/react');
        this.setState({
          tutoName:'React',
          imageIcon:'https://cdn.iconscout.com/icon/free/png-256/react-1-282599.png'
        })
        break;
      case '/redux':
           res = api.concat('/redux');
           this.setState({tutoName:'Redux'})
          break;
      default:
        return null;
    }
    fetch("https://tutorialweb-backend.herokuapp.com"+res)
      .then(res=>res.json())
      .then(response=>this.setState({tutorialIndex:response}));

      
  }


  render() {
    console.log('state imageIcon',this.state.imageIcon);
    return (
      <div>

        <ResponsiveDrawer
          imageIcon={this.state.imageIcon}
         tutoName={this.state.tutoName}
         tutorialIndex={this.state.tutorialIndex}
         />


      </div>
    );
  }
}

export default IndexTutorial;
