import React, { Component } from 'react';

import ResponsiveDrawer from './Layout';

class IndexTutorial extends Component {
  constructor(props){
    super(props)
    this.state={

      reactTutorialIndex:[],
      reduxTutorialIndex:[]
    }
    const api='/api';
    let res='';
    switch(this.props.match.path){
      case '/react':
        res = api.concat('/react');
        console.log(res);
        break;
      case '/redux':
           res = api.concat('/redux');
          console.log(res);
          break;

      default:
        return null;
    }
    fetch(res)
      .then(res=>res.json())
      .then(response=>this.setState({reactTutorialIndex:response}));

  }

  render() {


    return (
      <div>
        {console.log(this.props.match.path)}
        <ResponsiveDrawer tutoName="React" tutorialIndex={this.state.reactTutorialIndex} />
      </div>
    );
  }
}

export default IndexTutorial;
