import React from 'react'
import  { useState, useEffect } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';

class ReactHome extends React.Component {


  constructor(props){
    super(props)
    this.state={
      TopicContents:[]
    }
    fetch('/api/react/getLessons')
    .then(res=>res.json())
    .then(response=>this.setState({TopicContents:response}));

  }




render(){

  let componentVal = this;
  const urlParam=this.props.match.params;
  let tutorialName = this.state.TopicContents.filter(function(topic){
    return (topic.url_id === componentVal.props.match.params.topicId)
  })

  let script=`<script src`;
  return (

    <div>


    <br/><br/>
    <pre class="language-jsx"><code class="language-jsx" >
{`function Welcome(props) {
  return <h1>Hello, {props.name} </h1>;
}`}
</code></pre>



      {tutorialName.map(lesson=>(
        <div key={lesson._id} style={{width:'100%'}}>
        <div className="row">
          <div className="col-sm-9">
        <h1 style={{color: 'black',
        textAlign: 'center',
        fontSize: 30,
        paddingTop:20,
        fontFamily:'-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen-Sans,Ubuntu,Cantarell,Helvetica Neue,sans-serif'
        }}>
              {lesson.name}
        </h1><br/>
        <Divider/>

        {
          console.log(lesson.lesson_description.search(script))
        }

        <div style={{padding:'20px 20px 20px 50px',
        margin:'auto',
        lineHeight:'1.8em',
        width:'100%',
        height:'auto',
        textAlign:'justify'
        }}
        >
         {ReactHtmlParser(lesson.lesson_description)}
          </div>

        </div>
        </div>
        </div>
      ))}

    </div>
  )
}
}

export default ReactHome
